import React, {useState, useEffect, useDebugValue} from "react";
import axios from "axios";
import { Button, Dropdown, Menu, Input } from "antd";
import {
    EditOutlined,
    CaretDownFilled,
    DeleteOutlined,
    ExclamationCircleOutlined,
  } from "@ant-design/icons";
import DataTable from "./common/DataTable";

import EditPatientDialog from "./EditPatientDialog";
import DeletePatient from "./DeletePatient";
import { useHistory } from "react-router-dom";

const { Search } = Input;


function ViewEmployer(){

    let history = useHistory();

    const[employees, setEmployees] = useState([]);
    const [tableLoading, setTableLoading] = useState(false);

    const [editDialog, setEditDialog] = useState(() => ({ open: false }));
    const [deleteDialog, setDeleteDialog] = useState(() => ({ open: false }));

    useEffect(()=>{
      getPatients();
    }, [])

    function getPatients(){
      setTableLoading(true);
      axios.get("http://localhost:3005/employee/").then((res)=>{
          setEmployees(res.data);
          setTableLoading(false);
          console.log("data :", res.data);
      }).catch((err)=>{
          alert(err.message);
      })
    }


    function searchPatient(value){
      console.log("searched name : ", value);

      if(employees !== null){
        employees.filter((emp) => {
          return 
        })
      }
      // // setTableLoading(true);
      // axios.get(`http://localhost:3005/employee/${value}`).then((res)=>{
      //     // setEmployees(res.data.user);
      //     // setTableLoading(false);
      //     console.log("search data :", res.data.user);
      //     const newData = res.data.user;
      //     setEmployees([newData]);
      // }).catch((err)=>{
      //     alert(err.message);
      // })
    }


    const columns = [
        {
          title: "Patient ID",
          dataIndex: "pid",
          key: "pid",
        },
        {
          title: "Name",
          dataIndex: "name",
          key: "name",
        },
        {
          title: "Gender",
          dataIndex: "gender",
          key: "gender",
        },
        {
          title: "Age",
          dataIndex: "age",
          key: "age",
        },
        {
          title: "Height(cm)",
          dataIndex: "height",
          key: "height",
        },
        {
          title: "Weight(kg)",
          dataIndex: "weight",
          key: "weight",
        },
        {
          title: "Address",
          dataIndex: "address",
          key: "address",
        },
        {
          title: "Contact No",
          dataIndex: "phone",
          key: "phone",
        },
        {
          title: "Action",
          key: "id",
          width: 10,
          render: (record) => (
            <>
              <Dropdown
                overlay={
                  <Menu
                    selectable
                    defaultSelectedKeys={["1"]}
                    items={[
                      {
                        key: "Edit",
                        name: "Edit",
                        onClick: () => {
                        setEditDialog({ open: true, record: record });
                        //   setName(record.name);
                        //   setSerialNo(record.serial_no);
                        //   // console.log("evidence : ", record.serial_no);
                        },
                        icon: <EditOutlined />,
                        label: "Edit",
                      },
                      {
                        key: "Delete",
                        name: "Delete",
                        title: "Delete",
                        onClick: () => {
                          console.log("delete dialog");
                          setDeleteDialog({ open: true, record: record });
                        },
                        icon: <DeleteOutlined />,
                        label: "Delete",
                      },
                    ]}
                  />
                }
              >
                <Button
                  title="Actions"
                >
                  Action <CaretDownFilled />
                </Button>
              </Dropdown>
            </>
          ),
        },
      ];


      const onSearch = (value) => {
        console.log(value);
        searchPatient(value);
      }


    return(
            <div><br></br><br></br>

            <div style={{display:"flex", flexDirection:"row", justifyContent:"space-between"}}>

              <h3 style={{textAlign:"start", marginLeft:"85px"}}>Patients List</h3>

              <div style={{marginRight:"85px"}}>

                <Search
                  placeholder="search patient"
                  allowClear
                  onSearch={onSearch}
                  style={{
                    width: 300,
                  }}
                />

                <Button type="primary" style={{textAlign:"start", marginLeft:"85px"}} onClick={() => { history.push('/add') }} >Add Patient</Button>

              </div>

            </div>

            <div style={{ marginLeft:"80px", marginRight:"80px", border:"1px solid #B8E8FC", borderRadius:"10px", marginTop:"40px" }} >

                <DataTable
                loading={tableLoading}
                columns={columns}
                data={[...employees]}
                />

            </div>

