          key: "name",
        },
        {
          title: "Description",
          dataIndex: "description",
          key: "description",
        },
        {
          title: "Status",
          dataIndex: "is_active",
          key: "status",
          render: (_, { is_active }) => <StatusBatch active={is_active} />,
