        {
          title: "Action",
          key: "id",
          width: 10,
          render: (record) => (
            <>
              <Dropdown
                overlay={
                  <Menu
                    selectable
                    defaultSelectedKeys={["1"]}
                    items={[
                      {
                        key: "Edit",
                        name: "Edit",
                        onClick: () => {
                          setEditDialog({ open: true, id: record.id });
                          setName(record.name);
                          setSerialNo(record.serial_no);
                          // console.log("evidence : ", record.serial_no);
                        },
                        icon: <EditOutlined />,
                        label: "Edit",
                      },
                      {
                        key: "Delete",
                        name: "Delete",
                        title: "Delete",
                        onClick: () => {
                          setDeleteDialog({ open: true, id: record.id });
                        },
                        icon: <DeleteOutlined />,
                        label: "Delete",
                      },
                    ]}
                  />
                }
              >
                <Button
                  title="Actions"
                  className="border-2 border-borderbaction rounded-lg hover:bg-actionbtnhover hover:text-white hover:border-actionbtnhover pb-2"
                >
                  Action <CaretDownFilled />
                </Button>
              </Dropdown>
            </>
          ),
        },
      ];


    return(
            <div><br></br><br></br>
            <h3 style={{textAlign:"center"}}>View Employees</h3><br></br><br></br>

            

